
INSERT INTO `tbl_course`
VALUES (1, 'admin', '2024-09-26 15:57:47', '2024-09-30 08:50:36', NULL, 'KỸ THUẬT LẬP TRÌNH NỀN TẢNG VỚI JAVA', 200,
        'https://www.sqcacademy.com/_next/image?url=https%3A%2F%2Fappmaster.io%2Fapi%2F_files%2FhRaLG2N4DVjRZJQzCpN2zJ%2Fdownload%2F&w=1080&q=75',
        'Khóa học \"Kỹ thuật lập trình nền tảng với Java\" cung cấp kiến thức đa chiều về Java: từ cài đặt môi trường, cú pháp căn bản đến điều kiện, lặp, phương thức, Debugging, xử lý chuỗi, và mảng đến ArrayList. Nền tảng lý thú để xây dựng ứng dụng Java chất lượng.',
        'USD', 1, 0, 0, 30, 50),
       (2,'admin', '2024-09-26 15:57:47', '2024-09-30 08:50:36', NULL, 'KỸ THUẬT LẬP TRÌNH NỀN TẢNG VỚI C/C++', 200,
        'https://www.sqcacademy.com/_next/image?url=https%3A%2F%2Fimg-c.udemycdn.com%2Fcourse%2F750x422%2F3893308_0791.jpg&w=1080&q=75',
        'Khóa học giúp bạn xây dựng nền tảng vững chắc trong lập trình C/C++ và phát triển tư duy lập trình mãnh mẽ. Khóa học tập trung vào luyện kỹ năng khắc phục lỗi một cách nhanh chóng và cung cấp một lộ trình học bài bản và chuyên nghiệp.',
        'USD', 1, 0, 0, 30, 50),
       ( 3,'admin', '2024-09-26 15:57:47', '2024-09-30 08:50:36', NULL,
        'PHƯƠNG PHÁP LẬP TRÌNH HƯỚNG ĐỐI TƯỢNG VỚI JAVA', 200,
        'https://www.sqcacademy.com/_next/image?url=https%3A%2F%2Fraygun.com%2Fblog%2Fimages%2Foop-concepts-java%2Ffeature.png&w=1080&q=75',
        'Khóa học "Phương pháp lập trình hướng đối tượng với Java" cung cấp kiến thức chuyên sâu về lập trình OOP. Từ constructor, kế thừa, đa hình đến generic, exception và biểu thức chính quy, giúp học viên xây dựng ứng dụng Java hiệu quả ',
        'USD', 1, 0, 0, 30, 50);


