      n[i] = i + <span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">100</span>;
      <span class="hljs-comment" style="border-width:0px;font:inherit;margin:0px;padding:0px;">//thiet lap phan tu tai vi tri i la i + 100 </span>
   }
   cout &lt;&lt; <span class="hljs-string" style="border-width:0px;font:inherit;margin:0px;padding:0px;">"Phan tu thu:"</span> &lt;&lt; <span class="hljs-built_in" style="border-width:0px;font:inherit;margin:0px;padding:0px;">setw</span>(<span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">13</span>) &lt;&lt; <span class="hljs-string" style="border-width:0px;font:inherit;margin:0px;padding:0px;">"Gia tri la:"</span> &lt;&lt; endl;
   <span class="hljs-comment" style="border-width:0px;font:inherit;margin:0px;padding:0px;">//hien thi gia tri cua moi phan tu </span>
   <span class="hljs-keyword" style="border-width:0px;font:inherit;margin:0px;padding:0px;">for</span> (<span class="hljs-type" style="border-width:0px;font:inherit;margin:0px;padding:0px;">int</span> j = <span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">0</span>; j &lt; <span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">10</span>; j++)
   {
      cout &lt;&lt; <span class="hljs-built_in" style="border-width:0px;font:inherit;margin:0px;padding:0px;">setw</span>(<span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">7</span>)&lt;&lt; j &lt;&lt; <span class="hljs-built_in" style="border-width:0px;font:inherit;margin:0px;padding:0px;">setw</span>(<span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">13</span>) &lt;&lt; n[j] &lt;&lt; endl;
   } <span class="hljs-keyword" style="border-width:0px;font:inherit;margin:0px;padding:0px;">return</span> <span class="hljs-number" style="border-width:0px;font:inherit;margin:0px;padding:0px;">0</span>; }</pre><div class="adbox in-article adsense" style="-webkit-text-stroke-width:0px;background-color:rgba(248, 248, 248, 0.41);border:1px solid rgb(238, 238, 238);clear:both;color:rgba(0, 0, 0, 0.87);font-family:Arial, sans-serif;font-feature-settings:inherit;font-kerning:inherit;font-optical-sizing:inherit;font-size-adjust:inherit;font-size:16px;font-stretch:inherit;font-style:normal;font-variant-alternates:inherit;font-variant-caps:normal;font-variant-east-asian:inherit;font-variant-ligatures:normal;font-variant-numeric:inherit;font-variant-position:inherit;font-variation-settings:inherit;font-weight:400;letter-spacing:normal;line-height:inherit;margin:20px auto;orphans:2;padding:0px;position:relative;text-align:center;text-decoration-color:initial;text-decoration-style:initial;text-decoration-thickness:initial;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;" id="articleads2"><div style="background-color:transparent;border-style:none;display:inline-block;font:inherit;height:0px;margin:0px;opacity:0;overflow:hidden;padding:0px;position:relative;visibility:visible;width:694px;" id="aswift_2_host"><ins class="adsbygoogle" style="border-width:0px;display:block;font:inherit;height:0px;margin:0px;padding:0px;text-align:center;vertical-align:top;" data-ad-format="fluid" data-ad-layout="in-article" data-ad-client="ca-pub-9275417305531302" data-ad-slot="4889239415" data-adsbygoogle-status="done" data-ad-status="unfilled"><iframe style="border-width:0px;display:block;font:inherit;height:0px;left:0px;margin:auto;max-width:100%;padding:0px;position:absolute;top:0px;width:694px;" id="aswift_2" name="aswift_2" browsingtopics="true" sandbox="allow-forms allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts allow-top-navigat